(9318, 538, 'trip_itinerary', 'Day 01: Arrival and transfer to hotel\r\n\r\nDay 02: Drive from Kathmandu to Arughat Bazaar (535 m, 8hrs)\r\n\r\nDay 03: Trek Arughat to Soti Khola (6-7 hours)\r\n\r\nDay 04: Trek Soti Khola to Machha Khola 930m (7 hrs)\r\n\r\nDay 05: Trek Machha Khola to Doban (6-7 hours)\r\n\r\nDay 06: Trek Doban to Philim (6 hours)\r\n\r\nDay 07: Trek Philim to Chumling 2361m (Lower Tsum) (7 hours)\r\n\r\nDay 08: Trek Chumling to Chhokangparo 3010m (Upper Tsum) 6 hrs\r\n\r\nDay 09: Trek Chhokangparo to Nile/Chhule (3361m), en-route visit Milarepa Piren Phu Cave (7 hrs)\r\n\r\nDay 10: Trek to Mu Gompa (3700 m), visit Dhephyudonma Gompa (4060 m, 4 hrs)\r\n\r\nDay 11: Extra day for excursion\r\n\r\nDay 12: Trek Mu Gompa to Rachen Gompa (5 hours)\r\n\r\nDay 13: Trek Rachen Gompa to Dumje 7 hrs\r\n\r\nDay 14: Trek Dumje to Philim (cross Ripchet) (7 hrs)\r\n\r\nDay 15: Trek Philim to Tatopani 7 hrs\r\n\r\nDay 16: Trek Tatopani to Soti Khola 7 hrs\r\n\r\nDay 17: Drive from Soti Khola to Kathmandu\r\n\r\nDay 18: Kathmandu sightseeing\r\n\r\nDay 19: Final Departure from Kathmandu'),
(9319, 538, '_trip_itinerary', 'field_58c80c7448611'),
(9320, 538, 'cost_includes', '<strong>Services Includes</strong>\r\n<ul>\r\n 	<li>Airport pick up &amp; transfer by private transport</li>\r\n 	<li>Sightseeing in Kathmandu</li>\r\n 	<li>3 nights hotel accommodations in Kathmandu on BB basis</li>\r\n 	<li>Land transportation</li>\r\n 	<li>Tsum valley special trekking permit</li>\r\n 	<li>Manaslu conservation area permit</li>\r\n 	<li>All supporting staff (trekking guide and porters), as well as their necessary accommodation, meals, allowances and insurance,</li>\r\n 	<li>All camping equipment such as tents, Dining tent, Kitchen Tent, Toilet Tent, Table, chairs, Mattresses etc</li>\r\n 	<li>3 meals a day during the trek</li>\r\n 	<li> Accommodation in basic teahouse/monastery during the trek</li>\r\n</ul>'),
(9321, 538, '_cost_includes', 'field_58c80ca048613'),
(9322, 538, 'cost_excludes', '<strong>Service Excludes</strong>\r\n<ul>\r\n 	<li>Nepal entry Visa</li>\r\n 	<li>Lunch and dinner in Kathmandu</li>\r\n 	<li>Your travel/ medical insurance</li>\r\n 	<li>Emergency evacuation cost by Helicopter</li>\r\n 	<li>Your personal expenses such as telephone calls, alcoholic beverages, bottled drinks (including cold drinks and mineral water/boiled water), donation &amp; tips etc.</li>\r\n</ul>'),
