(9785, 557, 'cost_includes', '<strong>Services Includes</strong>\r\n<ul>\r\n 	<li>Airport pick up &amp; transfer by private transport</li>\r\n 	<li>Sightseeing in Kathmandu</li>\r\n 	<li>4 nights hotel accommodations in Kathmandu on BB basis</li>\r\n 	<li>Land transportation</li>\r\n 	<li>TIMS</li>\r\n 	<li>All supporting staff (trekking guide and porters), as well as their necessary accommodation, meals, allowances and insurance,</li>\r\n 	<li>All camping equipment such as two men tents, Dining tent, Kitchen Tent, Toilet Tent, Table, chairs, Mattresses etc</li>\r\n 	<li>Per porter carries 20 Kg. luggage while trekking</li>\r\n 	<li>Medical Kit Box</li>\r\n 	<li>3 meals a day during the trek</li>\r\n</ul>'),
(9786, 557, '_cost_includes', 'field_58c80ca048613'),
(9787, 557, 'cost_excludes', '<strong>Service Excludes</strong>\r\n<ul>\r\n 	<li>Nepal entry Visa</li>\r\n 	<li>Lunch and dinner in Kathmandu</li>\r\n 	<li>Your travel/ medical insurance</li>\r\n 	<li>Emergency evacuation cost by Helicopter</li>\r\n 	<li>Your personal expenses such as telephone calls, alcoholic beverages, bottled drinks (including cold drinks and mineral water/boiled water), donation &amp; tips etc.</li>\r\n 	<li>Tips for the trekking crew</li>\r\n</ul>'),
