(12588, 769, '_form', '<div class=\"modal-header\">\n  <button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-label=\"Close\"> <span aria-hidden=\"true\">&times;</span> </button>\n  <h4 class=\"modal-title\" id=\"myModalLabel\">Please fill up the form given below for quick inquiry and free quotes.</h4>\n</div>\n<div class=\"modal-body\">\n    <div class=\"formbox\">\n[response]\n      <p><span class=\"writespace\"><b>Inquiry For *:</b></span>[dynamictext* qenqiry-trip-dynamicname \"CF7_get_post_var key=\'title\'\"]\n      </p>\n	<p><span class=\"writespace\"><b>No of persons *:</b></span>[number* qenqiry-no-of-persons]\n      </p>\n      <p><span class=\"writespace\"><b>Full Name *:</b></span>[text* qenqiry-full-name]\n      </p>\n      <p><span class=\"writespace\"><b>Email *:</b></span>[email* qenqiry-email]\n      </p> \n      <p><span class=\"writespace\"><b>Nationality:</b></span>[text qenqiry-nationality]\n      </p>\n      <p><span class=\"writespace\"><b>Arrival Date:</b></span>[date qenqiry-arrival-date min:today+1days]\n      </p>\n      <p><span class=\"writespace\"><b>Your message to us:</b></span>[textarea qenqiry-comments x5]\n      </p>\n    </div>\n</div>\n<div class=\"modal-footer\">\n[submit class:btn class:btn-primary \"Submit\"]\n</div>'),
(12589, 769, '_mail', 'a:9:{s:6:\"active\";b:1;s:7:\"subject\";s:31:\"Quick Inquiry - \"[qi-question]\"\";s:6:\"sender\";s:41:\"[qi-name] <contact@highhimalayatreks.com>\";s:9:\"recipient\";s:22:\"sunilman2001@gmail.com\";s:4:\"body\";s:780:\"<h1 style=\"color:#337AB7; font-size:28px;\">Someone is looking for more information.</h1>\n<p style=\"border-bottom:1px solid #337AB7; padding-bottom:15px; margin-bottom:15px; font-size:18px;\">Below are the details:-</p>\n   <tr>\n    <td style=\"background:#f5f5f5; border:1px solid #eee; width:250px;\">Full Name:</td>\n    <td>[qi-name]</td>\n  </tr>\n   <tr>\n    <td style=\"background:#f5f5f5; border:1px solid #eee;\">Phone:</td>\n    <td>[qi-phone]</td>\n  </tr>\n   <tr>\n    <td style=\"background:#f5f5f5; border:1px solid #eee;\">Query:</td>\n    <td>[qi-question]</td>\n  </tr>    \n</table>\n<div style=\"padding:10px; background:#f2f2f2; font-size:14px; color:#666; font-style:italic; margin:15px 0;\">This e-mail was sent via contact form in the website http://highhimalayatreks.com/</div>\";s:18:\"additional_headers\";s:0:\"\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:1;s:13:\"exclude_blank\";b:0;}'),
