(16072, 1013, 'trip_itinerary', '<strong>Day 01:</strong> Arrive in Kathmandu and transfer to hotel\r\n\r\n<strong>Day 02:</strong> Drive from Kathmandu to Budhanilkantha and trek to Shivapuri\r\n\r\n<strong>Day 03:</strong> Trek Chisapani (approx. 5hrs)\r\n\r\n<strong>Day 04:</strong> Trek to Nagarkot (approx 6hrs)\r\n\r\n<strong>Day 05:</strong> Trek to Dhulikhel (6hrs)\r\n\r\n<strong>Day 06:</strong> Trek to Namo Buddha (approx 5hrs) &amp; drive to Kathmandu\r\n\r\n<strong>Day 07:</strong> Kathmandu sightseeing\r\n\r\n<strong>Day 08:</strong> Transfer to airport for final departure'),
(16073, 1013, '_trip_itinerary', 'field_58c80c7448611'),
(16074, 1013, 'cost_includes', '<ul>\r\n 	<li>Airport pick up and transfer to hotel</li>\r\n 	<li>Three night\'s hotel accommodation in Kathmandu with breakfast</li>\r\n 	<li>Accommodation in lodge along the trekking route</li>\r\n 	<li>English speaking local trekking guide</li>\r\n 	<li>Necessary porters to carry your baggage during the trekking.( Two persons get one trekking porter)</li>\r\n 	<li>Three time meals during the trekking- Breakfast, Lunch &amp; Dinner</li>\r\n 	<li>Shivapuri National Park permit</li>\r\n 	<li>Trekker’s Information Management System (TIMS) Card</li>\r\n 	<li>Private transport</li>\r\n 	<li>Insurance for Nepalese staff. (Trekking guide and porters)</li>\r\n 	<li>Trekking Guide and porters\' salary, food, transport, accommodations</li>\r\n 	<li>Medical supplies (first aid kit will be available)</li>\r\n 	<li>Government Tax including VAT and office service charge</li>\r\n 	<li>Airport drop at the time of final departure</li>\r\n</ul>'),
(16075, 1013, '_cost_includes', 'field_58c80ca048613'),
