(17407, 1037, 'trip_itinerary', '<strong>Day 01:</strong> Arrive Kathmandu &amp; transfer to hotel\r\n<strong>Day 02:</strong> Fly to Lukla and trek to Chutenga (3050m)\r\n<strong>Day 03:</strong> Cross Chhetra La pass (4580m) and trek to Chhetra Buk\r\n<strong>Day04:</strong> Trek to Thasingdingma\r\n<strong>Day05:</strong> Trek to Kothe (3500m)\r\n<strong>Day 06:</strong> Kothe to Thangnag (4350m)\r\n<strong>Day 07:</strong> Rest/ Acclimatisation Day at Thangnag (4350m)\r\n<strong>Day 08:</strong> Trek to Khare (5000m)\r\n<strong>Day 09:</strong> Khare to High Camp (5800m)/ Mera La pass (5400m)\r\n<strong>Day 10:</strong> Summit Mera Peak (6476m), return to Mera La (5415m)/Khare\r\n<strong>Day 11:</strong> Rest or spare day\r\n<strong>Day 12:</strong> Mera La / Khare to Thangnag (4350m)\r\n<strong>Day 13:</strong> Thangnag to Kothe\r\n<strong>Day 14:</strong> Kothe to Thasingdingma\r\n<strong>Day15:</strong> Thasingdingma to Zatra Buk\r\n<strong>Day16:</strong> Zatra Buk to Chutenga\r\n<strong>Day 17:</strong> Chutenga to Lukla (2850m)\r\n<strong>Day 18:</strong> Fly to Kathmandu\r\n<strong>Day 19:</strong> Kathmandu sightseeing\r\n<strong>Day 20:</strong> Final departure'),
(17408, 1037, '_trip_itinerary', 'field_58c80c7448611'),
(17409, 1037, 'cost_includes', '<ul>\r\n 	<li>Airport pick up &amp; drop by a private vehicle</li>\r\n 	<li>3 nights hotel accommodation in Kathmandu with breakfast</li>\r\n 	<li>Guided sightseeing in Kathmandu by private vehicles</li>\r\n 	<li>Tea house accommodation (lodge) during the trek</li>\r\n 	<li>A local government licensed English speaking trekking guide</li>\r\n 	<li>Necessary number of porters for carrying luggage during the trek</li>\r\n 	<li>Food, accommodation, salary, insurance, equipment, medicine for our trekking crew</li>\r\n 	<li>Sagarmatha National Park permit</li>\r\n 	<li>TIMS Card</li>\r\n 	<li>Mera Peak Permit fee</li>\r\n 	<li>Garbage Deposit fee.</li>\r\n 	<li>Climbing equipment<strong>:</strong> Rope, Ice Screw, Snow Bar etc.</li>\r\n 	<li>Flight fare Kathmandu – Lukla - Kathmandu including domestic airport tax</li>\r\n 	<li>Surface transfer to/ from Kathmandu- hotel before and after the trek</li>\r\n 	<li>All our government taxes</li>\r\n</ul>'),
