(18286, 1046, 'trip_itinerary', '<strong>Day 01:</strong>Arrival and transfer to hotel\r\n<strong>Day02:</strong>  Drive from Kathmandu to Beni (810m) (10hrs)\r\n<strong>Day03:</strong>Trek from Beni to Babichoaur (950m, 5hrs)\r\n<strong>Day04: </strong> Trek from Babiachaur to Dharapani 6hrs\r\n<strong>Day05: </strong> Trek to Muri 7hrs\r\n<strong>Day06:</strong>Trek to Baghar (2080m)\r\n<strong>Day07: </strong> Trek to Dobang (2520m, 5hrs)\r\n<strong>Day08: </strong>Trek to Sallaghari (3110m, 6hrs)\r\n<strong>Day09:</strong>  Trek to Upper Vungini/ Italian Base Camp (3660m, 6hrs)\r\n<strong>Day10:</strong> Trek to Glacier Camp /French Camp (4940m, 4hrs)\r\n<strong>Day11: </strong>Trek to Dhaulagiri Base Camp (4740m, 5hrs)\r\n<strong>Day12:</strong>  Trek to French Col (5010m) or Hidden Valley (5200m, 4hrs)\r\n<strong>Day13:</strong>Trek to Yak Kharka (7hrs)\r\n<strong>Day14:</strong> Trek to Marpha (2670m, 5hrs)\r\n<strong>Day15:</strong> Trek to Ghasa (6hrs)\r\n<strong>Day16:</strong>Trek to Tatopani (1189m, 5hrs)\r\n<strong>Day17:</strong> Trek to Ghorepani (2853m, 7hrs)\r\n<strong>Day18:</strong> Trek to Tadapani.\r\n<strong>Day19:</strong>Trek to Ghandruk (1950m)\r\n<strong>Day20:</strong> Trek to Pothana (1990m,6 hrs)\r\n<strong>Day21:</strong>Trek to Dhampus Phedi (113m, 4hrs) &amp; drive to Pokhara\r\n<strong>Day22:</strong>Free day in Pokhara\r\n<strong>Day23:</strong> Drive from Pokhara to Kathmandu (7 hrs)\r\n<strong>Day24: </strong> Kathmandu sightseeing and shopping\r\n<strong>Day25:</strong> Transfer to the Airport for final departure'),
(18287, 1046, '_trip_itinerary', 'field_58c80c7448611'),
(18288, 1046, 'cost_includes', '<ul>\r\n 	<li>Airport pick up and transfer to hotel</li>\r\n 	<li>Three night\'s hotel accommodation in Kathmandu with breakfast</li>\r\n 	<li>Two nights hotel accommodation in Pokhara with breakfast</li>\r\n 	<li>One night hotel accommodation in Beni with breakfast</li>\r\n 	<li>Accommodation in camps along the trekking route</li>\r\n 	<li>English speaking local trekking guide</li>\r\n 	<li>Necessary porters to carry your baggage during the trekking.( Two persons get one trekking porter)</li>\r\n 	<li>Equipment: Trekking tents, Dining tent, Kitchen Tent, Toilet Tent, Table and chairs, Mattresses during the trekking.</li>\r\n 	<li>Three time meals during the trekking- Breakfast, Lunch &amp; Dinner</li>\r\n 	<li>Annapurna Conservation Area entry permit</li>\r\n 	<li>Trekker’s Information Management system (TIMS)</li>\r\n 	<li>Private / local transport as per the program</li>\r\n 	<li>Insurance for Nepalese staff. (Trekking guide and porters)</li>\r\n 	<li>Trekking Guide and porters\' salary, food, drinks, accommodations</li>\r\n 	<li>Arrangement of Helicopter service for rescue in case of emergency in the mountain (the cost incurred during the rescue will be paid by your insurance company)</li>\r\n 	<li>Medical supplies (first aid kit will be available)</li>\r\n 	<li>Farewell Dinner before departure</li>\r\n 	<li>Government Tax and office service charge</li>\r\n 	<li>Airport drop at the time of departure</li>\r\n</ul>'),
(18289, 1046, '_cost_includes', 'field_58c80ca048613'),
(18290, 1046, 'cost_excludes', '<ul>\r\n 	<li>Lunch &amp; Dinner in Kathmandu &amp; Pokhara</li>\r\n 	<li>Nepal entry visa fee (you will easily get the visa upon your arrival at Tribhuwan International Airport)</li>\r\n 	<li>Monuments entry fee in Kathmandu during sightseeing</li>\r\n 	<li>Your international flight ticket and airport departure tax</li>\r\n 	<li>Personal equipments for Trekking</li>\r\n 	<li>High risk medical insurance</li>\r\n 	<li>Hot drinks, Alcoholic beverages, Telephone calls and laundry</li>\r\n 	<li>Soft drinks such as cola, mineral water</li>\r\n 	<li>Battery recharge, extra porters, shower etc during the trek</li>\r\n 	<li>Emergency rescue evacuation if needed (need to pay by your insurance)</li>\r\n 	<li>Extra night accommodation in Kathmandu or Pokhara because of early return from the trekking (due to any reason)</li>\r\n 	<li>Filmy permit for National park( Only for shooting camera)</li>\r\n 	<li>Tips &amp; Gratuities for trekking guide &amp; porter (s)</li>\r\n</ul>'),
