(20396, 1076, 'trip_itinerary', '<p style=\"margin: 0in; margin-bottom: .0001pt;\"><b><span style=\"font-size: 11.0pt; font-family: \'Tahoma\',sans-serif; color: black;\">Day 01:</span></b><span style=\"font-size: 11.0pt; font-family: \'Tahoma\',sans-serif; color: black;\"> Arrival Kathmandu &amp; transfer to hotel\r\n<b>Day 02:</b> Fly from Kathmandu to Lukla &amp; trek to Phakding.\r\n<b>Day 03:</b> Phakding to Namche Bazaar\r\n<b>Day 04</b>: Rest day at Namche Bazaar for acclimatization.\r\n<b>Day 05:</b> Namche to Tengboche.\r\n<b>Day 06:</b> Tengboche to Dingboche\r\n<b>Day07:</b> Rest/ exploration day at Dingboche visit Nangkharsang Gompa.\r\n<b>Day08:</b> Dingboche to Lobuche.\r\n<b>Day 09:</b> Lobuche to Kalapatter- Gorakshep\r\n<b>Day10:</b> Gorak Shap- EBC- Lobuche.\r\n<b>Day11:</b> Lobuche to Chhukung.\r\n<b>Day12:</b> Chhukung to Island Peak Base camp.\r\n<b>Day13:</b> Base camp to Summit &amp; back to Chhukung\r\n<b>Day14:</b> Chhukung  to Pangboche.\r\n<b>Day15:</b> Pangboche to Namche Bazaar\r\n<b>Day16: </b>Namche Bazaar to Lukla\r\n<b>Day17:</b> Fly Lukla to Kathmandu</span></p>\r\n<p style=\"margin: 0in; margin-bottom: .0001pt;\"><b><span style=\"font-size: 11.0pt; font-family: \'Tahoma\',sans-serif; color: black;\">Day18:</span></b><span style=\"font-size: 11.0pt; font-family: \'Tahoma\',sans-serif; color: black;\"> Kathmandu sightseeing\r\n<b>Day19:</b> Final departure </span></p>'),
(20397, 1076, '_trip_itinerary', 'field_58c80c7448611'),
(20398, 1076, 'cost_includes', '<ul>\r\n 	<li>Airport pick up &amp; drop by a private vehicle</li>\r\n 	<li>3 nights hotel accommodation in Kathmandu with breakfast</li>\r\n 	<li>Guided sightseeing in Kathmandu by private vehicles</li>\r\n 	<li>Tea house accommodation (lodge) during the trek</li>\r\n 	<li>A local government licensed English speaking trekking guide</li>\r\n 	<li>Necessary number of porters for carrying luggage during the trek</li>\r\n 	<li>Food, accommodation, salary, insurance, equipment, medicine for our trekking crew</li>\r\n 	<li>Sagarmatha National Park permit</li>\r\n 	<li>TIMS Card</li>\r\n 	<li>Island Peak Permit fee</li>\r\n 	<li>Garbage Deposit fee.</li>\r\n 	<li>Climbing equipment<strong>:</strong> Rope, Ice Screw, Snow Bar etc.</li>\r\n 	<li>Flight fare Kathmandu – Lukla - Kathmandu including domestic airport tax</li>\r\n 	<li>Surface transfer to/ from Kathmandu- hotel before and after the trek</li>\r\n 	<li>All our government taxes</li>\r\n</ul>'),
